import java.util.Arrays;
import java.util.List;
import org.junit.jupiter.api.Test;
import static org.junit.jupiter.api.Assertions.assertEquals;

class Conversation {
    private String sender;

    public Conversation(String sender) {
        this.sender = sender;
    }

    public String getSender() {
        return sender;
    }
}

class ConversationCounter {
    public static int countConversationTurns(List<Conversation> conversations, String doctor, String patient) {
        int turns = 0;
        boolean lastMessageFromDoctor = false;

        for (Conversation conversation : conversations) {
            if (conversation.getSender().equals(doctor)) {
                if (!lastMessageFromDoctor) {
                    turns++;
                }
                lastMessageFromDoctor = true;
            } else if (conversation.getSender().equals(patient)) {
                if (lastMessageFromDoctor) {
                    turns++;
                }
                lastMessageFromDoctor = false;
            }
        }

        return turns;
    }
}

class MainTest {
    @Test
    void testCountConversationTurns1() {
        List<Conversation> conversations = Arrays.asList(
                new Conversation("doctor"),
                new Conversation("patient"),
                new Conversation("patient"),
                new Conversation("doctor"),
                new Conversation("doctor"),
                new Conversation("patient"),
                new Conversation("patient")
        );
        assertEquals(3, ConversationCounter.countConversationTurns(conversations, "doctor", "patient"));
    }
}
