class MainTest {

    public static Set<String> generatePermutations(String input) {
        Set<String> result = new HashSet<>();
        if (input == null || input.length() == 0) {
            return result;
        }

        permutation("", input, result);
        return result;
    }

    private static void permutation(String prefix, String input, Set<String> result) {
        if (input.length() == 0) {
            result.add(prefix);
            return;
        }

        for (int i = 0; i < input.length(); i++) {
            String newPrefix = prefix + input.charAt(i);
            String newInput = input.substring(0, i) + input.substring(i + 1);
            permutation(newPrefix, newInput, result);
        }
    }

    @Test
    void testBasic() {
        assertEquals(Set.of("abc", "acb", "bac", "bca", "cab", "cba"), generatePermutations("abc"));
    }
}
