import org.junit.jupiter.api.Test;
import static org.junit.jupiter.api.Assertions.assertEquals;

public class MainTest {

    @Test
    void testShowQianFenWei() {
        assertEquals("2", Main.showQianFenWei(2));
        assertEquals("100", Main.showQianFenWei(100));
        assertEquals("1,000", Main.showQianFenWei(1000));
        assertEquals("10,000", Main.showQianFenWei(10000));
    }
}

class Main {
    public static String showQianFenWei(long x) {
        String result = String.valueOf(x);
        int length = result.length();
        if (length <= 3) {
            return result;
        }
        StringBuilder sb = new StringBuilder();
        for (int i = 0; i < length; i++) {
            if (i != 0 && (length - i) % 3 == 0) {
                sb.append(',');
            }
            sb.append(result.charAt(i));
        }
        return sb.toString();
    }
}
