import static org.junit.jupiter.api.Assertions.assertArrayEquals;

public class MainTest {
    public static class MatrixOperations {
        public static boolean[][] meet(boolean[][] matrix1, boolean[][] matrix2) {
            if (matrix1.length != matrix2.length || matrix1[0].length != matrix2[0].length) {
                throw new IllegalArgumentException("Matrices must have the same dimensions");
            }

            boolean[][] result = new boolean[matrix1.length][matrix1[0].length];
            for (int i = 0; i < matrix1.length; i++) {
                for (int j = 0; j < matrix1[0].length; j++) {
                    result[i][j] = matrix1[i][j] && matrix2[i][j];
                }
            }
            return result;
        }

        public static boolean[][] join(boolean[][] matrix1, boolean[][] matrix2) {
            if (matrix1.length != matrix2.length || matrix1[0].length != matrix2[0].length) {
                throw new IllegalArgumentException("Matrices must have the same dimensions");
            }

            boolean[][] result = new boolean[matrix1.length][matrix1[0].length];
            for (int i = 0; i < matrix1.length; i++) {
                for (int j = 0; j < matrix1[0].length; j++) {
                    result[i][j] = matrix1[i][j] || matrix2[i][j];
                }
            }
            return result;
        }
    }

    @Test
    void testMeet1() {
        boolean[][] matrix1 = {{true, false}, {false, true}};
        boolean[][] matrix2 = {{false, true}, {true, false}};
        boolean[][] expected = {{false, false}, {false, false}};
        assertArrayEquals(expected, MatrixOperations.meet(matrix1, matrix2));
