        boolean[][] matrix1 = {{true, false}, {false, true}};
        boolean[][] matrix2 = {{false, true}, {true, false}};
        boolean[][] expected = {{true, true}, {true, true}};
        assertArrayEquals(expected, MatrixOperations.join(matrix1, matrix2));
    }
}
