        Set<String> values = new HashSet<>();
        String[] parts = input.split(";");
        for (String part : parts) {
            String[] subParts = part.split("&&");
            if (subParts.length > 1) {
                String value = subParts[subParts.length - 1];
                if (RiskRejectCategoryEnum.contains(value)) {
                    values.add(value);
                }
            }
        }
        return values;
