public class Main {
    public static int maxOperations(Queue<Integer> queue) {
        int count = 0;
        PriorityQueue<Integer> pq = new PriorityQueue<>();
        while (!queue.isEmpty()) {
            int num = queue.poll();
            if (pq.isEmpty() || pq.peek() != num) {
                pq.add(num);
            } else {
                pq.poll();
                count++;
            }
        }
        return count * 2;
    }
}
