        while ((line = reader.readLine()) != null) {
            // Remove comments from the line
            Matcher matcher = pattern.matcher(line);
            line = matcher.replaceAll("");

            // Write the line to the output file
            writer.write(line + "\n");
        }

        reader.close();
        writer.close();
    }
}
