public class MaxLCMGCD {
    public static int maxLCMMinusGCD(int N) {
        // Create a list of goods
        List<Goods> goods = new ArrayList<>();

        // Generate random goods
        Random random = new Random();
        for (int i = 0; i < N; i++) {
            int id = random.nextInt(1000);
            String name = "Goods " + i;
            int price = random.nextInt(1000);
            goods.add(new Goods(id, name, price));
        }

        // Sort the goods by id, name, and price
        Collections.sort(goods, new Comparator<Goods>() {
            @Override
            public int compare(Goods g1, Goods g2) {
                if (g1.getId() != g2.getId()) {
                    return g1.getId() - g2.getId();
                } else if (!g1.getName().equals(g2.getName())) {
                    return g1.getName().compareTo(g2.getName());
                } else {
                    return g1.getPrice() - g2.getPrice();
                }
            }
        });

        // Print the sorted goods
        for (Goods good : goods) {
            System.out.println(good);
        }

        return 0;
    }

    public static class Goods {
        private int id;
        private String name;
        private int price;

        public Goods(int id, String name, int price) {
            this.id = id;
            this.name = name;
            this.price = price;
        }

        public int getId() {
            return id;
        }

        public String getName() {
            return name;
        }

        public int getPrice() {
            return price;
        }

        @Override
        public String toString() {
            return "Goods{" +
                    "id=" + id +
                    ", name='" + name + '\'' +
                    ", price=" + price +
                    '}';
        }
    }
}
