import java.util.HashMap;
import java.util.Map;
import java.util.PriorityQueue;
import java.util.Queue;

public class Huffman {
    private String message;
    private String encodedMessage;

    public void send(String message) {
        this.message = message;
        this.encodedMessage = encode(message);
    }

    public String recv() {
        return decode(encodedMessage);
    }

    private String encode(String message) {
        Map<Character, Integer> frequencyMap = new HashMap<>();
        for (char c : message.toCharArray()) {
            frequencyMap.put(c, frequencyMap.getOrDefault(c, 0) + 1);
        }

        Queue<Node> priorityQueue = new PriorityQueue<>((a, b) -> a.frequency - b.frequency);
        for (Map.Entry<Character, Integer> entry : frequencyMap.entrySet()) {
            priorityQueue.add(new Node(entry.getKey(), entry.getValue()));
        }

        while (priorityQueue.size() > 1) {
            Node left = priorityQueue.remove();
            Node right = priorityQueue.remove();
            Node parent = new Node(null, left.frequency + right.frequency);
            parent.left = left;
            parent.right = right;
            priorityQueue.add(parent);
        }

        Node root = priorityQueue.remove();
        Map<Character, String> huffmanCode = new HashMap<>();
        buildHuffmanCode(root, "", huffmanCode);

        StringBuilder encodedMessage = new StringBuilder();
        for (char c : message.toCharArray()) {
            encodedMessage.append(huffmanCode.get(c));
        }

        return encodedMessage.toString();
    }

    private void buildHuffmanCode(Node root, String code, Map<Character, String> huffmanCode) {
        if (root.left == null && root.right == null) {
            huffmanCode.put(root.character, code);
        } else {
            buildHuffmanCode(root.left, code + "0", huffmanCode);
            buildHuffmanCode(root.right, code + "1", huffmanCode);
        }
    }

    private String decode(String encodedMessage) {
        Map<Character, Integer> frequencyMap = new HashMap<>();
        for (char c : message.toCharArray()) {
            frequencyMap.put(c, frequencyMap.getOrDefault(c, 0) + 1);
        }

        Queue<Node> priorityQueue = new PriorityQueue<>((a, b) -> a.frequency - b.frequency);
        for (Map.Entry<Character, Integer> entry : frequencyMap.entrySet()) {
            priorityQueue.add(new Node(entry.getKey(), entry.getValue()));
        }

        while (priorityQueue.size() > 1) {
            Node left = priorityQueue.remove();
            Node right = priorityQueue.remove();
            Node parent = new Node(null, left.frequency + right.frequency);
            parent.left = left;
            parent.right = right;
            priorityQueue.add(parent);
        }

        Node root = priorityQueue.remove();
        Map<String, Character> huffmanCode = new HashMap<>();
        buildHuffmanCode(root, "", huffmanCode);

        StringBuilder decodedMessage = new StringBuilder();
        for (int i = 0; i < encodedMessage.length();) {
            String code = "";
            while (huffmanCode.containsKey(code)) {
                code += encodedMessage.charAt(i);
