import java.util.List;
import java.util.Arrays;

public class Conversation {
    private String sender; // 发送者

    public Conversation(String sender) {
        this.sender = sender;
    }

    public String getSender() {
        return sender;
    }
}

public class ConversationCounter {
    public static int countConversationTurns(List<Conversation> conversations, String doctor, String patient) {
        if (conversations == null || conversations.isEmpty()) {
            return 0;
        }

        int count = 0;
        String lastSender = conversations.get(0).getSender();
        for (Conversation conversation : conversations) {
            String currentSender = conversation.getSender();
            if (!currentSender.equals(lastSender)) {
                count++;
                lastSender = currentSender;
            }
        }

        return count;
    }

    public static void main(String[] args) {
        List<Conversation> conversations = Arrays.asList(
                new Conversation("doctor"),
                new Conversation("patient"),
                new Conversation("patient"),
                new Conversation("doctor"),
                new Conversation("doctor"),
                new Conversation("patient"),
                new Conversation("patient")
        );

        System.out.println(countConversationTurns(conversations, "doctor", "patient")); // 输出：3
    }
}

