public class Main {
    public static void main(String[] args) {
        String text = "全部已关闭待支付交易成功订单号: 100e863a2b86402a881b62abee3e337e交易成功客户账号: 13422344170产品名称:代服管家￥200.00";
        String result = findFirstAlphabeticStringAfterOrder(text);

        if (result != null) {
            System.out.println("找到的字符串: " + result);
        } else {
            System.out.println("没有找到匹配的字符串");
        }
    }

    public static String findFirstAlphabeticStringAfterOrder(String text) {
        String regex = "订单.*?([a-zA-Z]+)";
        Pattern pattern = Pattern.compile(regex);
        Matcher matcher = pattern.matcher(text);

        if (matcher.find()) {
            return matcher.group(1);
        } else {
            return null;
        }
    }
