import java.util.*;

class Task {
    String name;
    Date deadline;

    public Task(String name, Date deadline) {
        this.name = name;
        this.deadline = deadline;
    }

    @Override
    public String toString() {
        return "Task{" +
                "name='" + name + '\'' +
                ", deadline=" + deadline +
                '}';
    }
}

class Model {
    List<Task> tasks;

    public Model() {
        this.tasks = new ArrayList<>(Collections.nCopies(10, null));
    }

    public void addTask(Task task) {
        for (int i = 0; i < tasks.size(); i++) {
            if (tasks.get(i) == null) {
                tasks.set(i, task);
                break;
            }
        }
    }

    public void deleteTask(String taskName) {
        tasks.removeIf(task -> task != null && task.name.equals(taskName));
    }

    public void viewTasks() {
        for (Task task : tasks) {
            System.out.println(task);
        }
    }
}

class Controller {
    Model model;

    public Controller(Model model) {
        this.model = model;
    }

    public void addTask(String name, Date deadline) {
        model.addTask(new Task(name, deadline));
    }

    public void deleteTask(String name) {
        model.deleteTask(name);
    }

    public void viewTasks() {
        model.viewTasks();
    }
}

public class Main {
    public static void main(String[] args) {
        Model model = new Model();
        Controller controller = new Controller(model);

        // Test view tasks
        controller.viewTasks();

        // Test add tasks
        controller.addTask("Task 1", new Date());
        controller.addTask("Task 2", new Date());

        // Test view tasks
        controller.viewTasks();

        // Test delete tasks
        controller.deleteTask("Task 1");

        // Test view tasks
        controller.viewTasks();
    }
}

