public class Main {
    public static void main(String[] args) {
        int[] result = calculateProportion(0.3, 0.4, 0.3, 100);
        for (int i : result) {
            System.out.println(i);
        }
    }

    public static int[] calculateProportion(double ratio1, double ratio2, double ratio3, int total) {
        int[] result = new int[3];
        result[0] = (int) Math.floor(ratio1 * total);
        result[1] = (int) Math.floor(ratio2 * total);
        result[2] = (int) Math.floor(ratio3 * total);

        int sum = result[0] + result[1] + result[2];
        int diff = total - sum;

        while (diff > 0) {
            if (ratio1 >= ratio2 && ratio1 >= ratio3) {
                result[0] += diff;
            } else if (ratio2 >= ratio1 && ratio2 >= ratio3) {
                result[1] += diff;
            } else {
                result[2] += diff;
            }
            diff--;
        }

        return result;
    }
}

