import java.util.HashSet;
import java.util.Set;

public class StringPermutations {
    public static void main(String[] args) {
        String str = "aaa";
        permuteAndPrint(str);
    }

    public static void permuteAndPrint(String str) {
        Set<String> permutations = new HashSet<>();
        permute(str.toCharArray(), 0, permutations);

        for (String s : permutations) {
            System.out.println(s);
        }
    }

    public static void permute(char[] chars, int currentIndex, Set<String> permutations) {
        if (currentIndex == chars.length - 1) {
            permutations.add(new String(chars));
        }

        for (int i = currentIndex; i < chars.length; i++) {
            swap(chars, currentIndex, i);
            permute(chars, currentIndex + 1, permutations);
            swap(chars, currentIndex, i); // backtrack
        }
    }

    private static void swap(char[] array, int i, int j) {
        char temp = array[i];
        array[i] = array[j];
        array[j] = temp;
    }
}

