    public static String showQianFenWei(long x) {
        String str = Long.toString(x);
        String result = "";
        int count = 0;
        for (int i = str.length() - 1; i >= 0; i--) {
            result = str.charAt(i) + result;
            count++;
            if (count % 3 == 0 && i != 0) {
                result = "," + result;
            }
        }
        return result;
    }
