import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        System.out.println("Enter the order of the matrix:");
        int n = scanner.nextInt();
        int[][] matrix = new int[n][n];
        System.out.println("Enter the elements of the matrix:");
        for (int i = 0; i < n; i++) {
            for (int j = 0; j < n; j++) {
                matrix[i][j] = scanner.nextInt();
            }
        }
        scanner.close();
        System.out.println("The sum of the elements excluding the anti-diagonal, last row and last column is: " + calculateSum(matrix, n));
    }

    static int calculateSum(int[][] matrix, int n) {
        int sum = 0;
        for (int i = 0; i < n; i++) {
            for (int j = 0; j < n; j++) {
                if (i != n - 1 && j != n - 1 && j != n - 1 - i) {
                    sum += matrix[i][j];
                }
            }
        }
        return sum;
    }
}

