import java.util.*;

class Alarm {
    int id;
    Set<Integer> weekdays;
    int hour;
    int minute;
    int typeId;

    public Alarm(int id, Set<Integer> weekdays, int hour, int minute, int typeId) {
        this.id = id;
        this.weekdays = weekdays;
        this.hour = hour;
        this.minute = minute;
        this.typeId = typeId;
    }
}

class AlarmSystem {
    Map<Integer, Alarm> alarms;

    public AlarmSystem() {
        this.alarms = new HashMap<>();
    }

    public boolean addAlarm(int id, Set<Integer> weekdays, int hour, int minute, int typeId) {
        if (alarms.containsKey(id)) {
            return false;
        }
        alarms.put(id, new Alarm(id, weekdays, hour, minute, typeId));
        return true;
    }

    public boolean deleteAlarm(int id) {
        if (!alarms.containsKey(id)) {
            return false;
        }
        alarms.remove(id);
        return true;
    }

    public List<Alarm> queryAlarm(int weekday, int hour, int startMinute, int endMinute) {
        List<Alarm> result = new ArrayList<>();
        for (Alarm alarm : alarms.values()) {
            if (alarm.weekdays.contains(weekday) && alarm.hour == hour && alarm.minute >= startMinute && alarm.minute <= endMinute) {
                result.add(alarm);
            }
        }
        Collections.sort(result, new Comparator<Alarm>() {
            @Override
            public int compare(Alarm a1, Alarm a2) {
                if (a1.minute != a2.minute) {
                    return a1.minute - a2.minute;
                }
                if (a1.typeId != a2.typeId) {
                    return a1.typeId - a2.typeId;
                }
                return a1.id - a2.id;
            }
        });
        return result;
    }
}

