import java.util.regex.Matcher;
import java.util.regex.Pattern;

public class SQLParser {
    private String select;
    private String from;
    private String where;
    private String orderBy;
    private String limit;

    public SQLParser(String sql) {
        parse(sql);
    }

    private void parse(String sql) {
        if (sql == null || sql.trim().isEmpty()) {
            select = from = where = orderBy = limit = null;
            return;
        }

        Pattern pattern = Pattern.compile("select\\s+(.*?)\\s+from\\s+(.*?)(?:\\s+where\\s+(.*?))?(?:\\s+order by\\s+(.*?))?(?:\\s+limit\\s+(.*?))?$", Pattern.CASE_INSENSITIVE);
        Matcher matcher = pattern.matcher(sql);
        if (matcher.find()) {
            select = matcher.group(1).trim();
            from = matcher.group(2).trim();
            where = matcher.group(3) != null ? matcher.group(3).trim() : null;
            orderBy = matcher.group(4) != null ? matcher.group(4).trim() : null;
            limit = matcher.group(5) != null ? matcher.group(5).trim() : null;
        } else {
            select = from = where = orderBy = limit = null;
        }
    }

    // Getters for select, from, where, orderBy, and limit
    // ...
    String getSelect() {
        return select;
    }
    String getFrom() {
        return from;
    }
    String getWhere(){
        return where;
    }
    String getOrderBy(){
        return orderBy;
    }
    String getLimit(){
        return limit;
    }
    public static void main(String[] args) {
        SQLParser parser = new SQLParser("select name, age from users where age > 18 order by age limit 10");
        System.out.println("Select: " + parser.getSelect());
        System.out.println("From: " + parser.getFrom());
        System.out.println("Where: " + parser.getWhere());
        System.out.println("Order By: " + parser.getOrderBy());
        System.out.println("Limit: " + parser.getLimit());
    }




}

