import java.util.ArrayList;

public class SeqList {
    ArrayList<Character> list;

    public SeqList() {
        this.list = new ArrayList<>();
    }

    public void createSeqList(char[] elements) {
        for (char element : elements) {
            this.list.add(element);
        }
