import java.time.LocalDate;
import java.time.temporal.ChronoUnit;
import java.util.Calendar;
import java.util.HashMap;
import java.util.Map;

public class DateDifference {
    private static final Map<String, Integer> CHINESE_NUMBERS = new HashMap<String, Integer>() {{
        put("一", 1);
        put("二", 2);
        put("三", 3);
        put("四", 4);
        put("五", 5);
        put("六", 6);
        put("七", 7);
        put("八", 8);
        put("九", 9);
        put("十", 10);
        put("十一", 11);
        put("十二", 12);
    }};

    public static int getDaysDifference(String chineseDate) {
        LocalDate now = LocalDate.now();
        int days = 0;

        if (chineseDate.contains("今天") || chineseDate.contains("今日")) {
            days = 1;
        } else if (chineseDate.contains("昨天") || chineseDate.contains("昨日")) {
            days = 2;
        } else if (chineseDate.contains("近")) {
            String numberStr = chineseDate.replaceAll("[^0-9一二三四五六七八九十]", "");
            int number = CHINESE_NUMBERS.containsKey(numberStr) ? CHINESE_NUMBERS.get(numberStr) : Integer.parseInt(numberStr);

            if (chineseDate.contains("周")) {
                days = number * 7;
            } else if (chineseDate.contains("天")) {
                days = number;
            } else if (chineseDate.contains("月")) {
                LocalDate pastDate = now.minusMonths(number);
                days = (int) ChronoUnit.DAYS.between(pastDate, now);
            }
        }

        return days;
    }

    public static void main(String[] args) {
        System.out.println(getDaysDifference("今天")); // 1
        System.out.println(getDaysDifference("昨天")); // 2
        System.out.println(getDaysDifference("近一周")); // 7
        System.out.println(getDaysDifference("近7天")); // 7
        System.out.println(getDaysDifference("近一月")); // depends on the current date
    }
}

