public class PathReader {

    public static String readPathTest(String path) {
        // 检查路径是否为空或null
        if (path == null || path.isEmpty()) {
            return "";
        }

        // 查找最后一个'/'的位置
        int lastSlashIndex = path.lastIndexOf('/');

        // 从最后一个'/'之后提取文件名（包括扩展名）
        String fileNameWithExtension = path.substring(lastSlashIndex + 1);

        // 查找文件名中'.'的位置，用以去除扩展名
        int dotIndex = fileNameWithExtension.indexOf('.');

        // 如果没有扩展名，直接返回文件名
        if (dotIndex == -1) {
            return fileNameWithExtension;
        }

        // 返回没有扩展名的文件名
        return fileNameWithExtension.substring(0, dotIndex);
    }

    // 可以在这里添加main方法来测试功能
    public static void main(String[] args) {
        String path = "/courses/lecture/Lecture01.pdf";
        System.out.println(readPathTest(path)); // 应该输出 "Lecture01"
    }
}

