public class MatrixOperations {
    public static boolean[][] meet(boolean[][] matrix1, boolean[][] matrix2) {
        int m = matrix1.length;
        int n = matrix1[0].length;
        boolean[][] result = new boolean[m][n];
        for (int i = 0; i < m; i++) {
            for (int j = 0; j < n; j++) {
                result[i][j] = matrix1[i][j] && matrix2[i][j];
            }
        }
        return result;
    }

    public static boolean[][] join(boolean[][] matrix1, boolean[][] matrix2) {
        int m = matrix1.length;
        int n = matrix1[0].length;
        boolean[][] result = new boolean[m][n];
        for (int i = 0; i < m; i++) {
            for (int j = 0; j < n; j++) {
                result[i][j] = matrix1[i][j] || matrix2[i][j];
            }
        }
        return result;
    }

    public static void main(String[] args) {
        boolean[][] matrix1 = {{true, false}, {false, true}};
        boolean[][] matrix2 = {{false, true}, {true, false}};
        boolean[][] meetResult = meet(matrix1, matrix2);
        boolean[][] joinResult = join(matrix1, matrix2);
        System.out.println("Meet Result:");
        for (boolean[] row : meetResult) {
            for (boolean value : row) {
                System.out.print(value + " ");
            }
            System.out.println();
        }
        System.out.println("Join Result:");
        for (boolean[] row : joinResult) {
            for (boolean value : row) {
                System.out.print(value + " ");
            }
            System.out.println();
        }
    }
}

