import java.util.Scanner;

abstract class People {
    protected double height;
    protected double weight;

    public People(double height, double weight) {
        this.height = height;
        this.weight = weight;
    }

    public double calculateBMI() {
        return weight / (height * height);
    }

    public abstract String conclusion();
}

class Man extends People {
    public Man(double height, double weight) {
        super(height, weight);
    }

    public String conclusion() {
        double bmi = calculateBMI();
        if (bmi < 20) {
            return "过轻";
        } else if (bmi < 25) {
            return "适中";
        } else if (bmi < 30) {
            return "过重";
        } else if (bmi < 35) {
            return "肥胖";
        } else {
            return "非常肥胖";
        }
    }
}

class Woman extends People {
    public Woman(double height, double weight) {
        super(height, weight);
    }

    public String conclusion() {
        double bmi = calculateBMI();
        if (bmi < 19) {
            return "过轻";
        } else if (bmi < 24) {
            return "适中";
        } else if (bmi < 29) {
            return "过重";
        } else if (bmi < 34) {
            return "肥胖";
        } else {
            return "非常肥胖";
        }
    }
}

