import java.io.File;
import java.io.FileReader;
import java.io.FileWriter;
import java.io.IOException;

public class FileEncoder {

    public static void encodeFile(File encodingFile, File encodedFile) {
        try (FileReader reader = new FileReader(encodingFile);
             FileWriter writer = new FileWriter(encodedFile)) {
            int c;
            while ((c = reader.read()) != -1) {
                char character = (char) c;
                if (Character.isDigit(character)) {
                    // 数字加密规则
                    character = character == '9' ? '0' : (char) (character + 1);
                } else if (Character.isLetter(character)) {
                    // 字母加密规则
                    if (character == 'z') {
                        character = 'a';
                    } else if (character == 'Z') {
                        character = 'A';
                    } else if ((character >= 'a' && character < 'z') || (character >= 'A' && character < 'Z')) {
                        character = (char) (character + 1);
                    }
                }
                // 非数字和字母的字符保持不变
                writer.write(character);
            }
        } catch (IOException e) {
            e.printStackTrace();
        }
    }

    public static void main(String[] args) {
        // 示例使用方法
        File encodingFile = new File("path/to/your/input.txt"); // 指定要加密的文件路径
        File encodedFile = new File("path/to/your/output.txt"); // 指定加密后文件的保存路径
        encodeFile(encodingFile, encodedFile);
    }
}

