import java.util.PriorityQueue;

public class KthLargestInMatrix {

    public int findKthLargestValue(int n, int m, int k) {
        // 验证输入
        if (n <= 0 || m <= 0 || k <= 0 || k > n * m) {
            return Integer.MIN_VALUE;
        }

        // 使用最小堆来存储最大的k个元素
        PriorityQueue<Integer> minHeap = new PriorityQueue<>(k);

        // 遍历所有单元格
        for (int i = 1; i <= n; i++) {
            for (int j = 1; j <= m; j++) {
                int value = i * j;

                // 如果堆的大小小于k，直接添加
                if (minHeap.size() < k) {
                    minHeap.add(value);
                } else if (value > minHeap.peek()) {
                    // 如果当前值大于堆顶元素，则弹出堆顶，将当前值加入堆中
                    minHeap.poll();
                    minHeap.add(value);
                }
            }
        }

        // 堆顶元素即为第k大的元素
        return minHeap.peek();
    }
}

