import java.util.ArrayList;
import java.util.Collections;
import java.util.HashSet;
import java.util.List;
import java.util.Set;

public class StringPermutations {

    public List<String> permute(String s) {
        Set<String> permutations = new HashSet<>();
        permute("", s, permutations);
        List<String> sortedList = new ArrayList<>(permutations);
        Collections.sort(sortedList);
        return sortedList;
    }

    private void permute(String prefix, String s, Set<String> permutations) {
        int n = s.length();
        if (n == 0) {
            permutations.add(prefix);
        } else {
            for (int i = 0; i < n; i++) {
                permute(prefix + s.charAt(i), s.substring(0, i) + s.substring(i + 1, n), permutations);
            }
        }
    }

    public static void main(String[] args) {
        StringPermutations sp = new StringPermutations();
        List<String> result = sp.permute("AAB");
        result.forEach(System.out::println);
        System.out.println("Total permutations: " + result.size());
    }
}

