import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        int num = scanner.nextInt();
        scanner.nextLine();
        Map<Integer, Set<Integer>> holdMap = new HashMap<>();
        Map<Integer, Set<Integer>> waitMap = new HashMap<>();
        for (int i = 0; i < num; i++) {
            String line = scanner.nextLine();
            String[] parts = line.split("\\s+");
            int pid = Integer.parseInt(parts[0]);
            Set<Integer> holdSet = new HashSet<>();
            Set<Integer> waitSet = new HashSet<>();
            for (String s : parts[1].substring(1, parts[1].length() - 1).split(",")) {
                if (!s.isEmpty()) {
                    holdSet.add(Integer.parseInt(s));
                }
            }
            for (String s : parts[2].substring(1, parts[2].length() - 1).split(",")) {
                if (!s.isEmpty()) {
                    waitSet.add(Integer.parseInt(s));
                }
            }
            holdMap.put(pid, holdSet);
            waitMap.put(pid, waitSet);
        }
        List<Integer> deadlocks = findDeadlocks(holdMap, waitMap);
        Collections.sort(deadlocks);
        System.out.println(deadlocks);
    }

    static List<Integer> findDeadlocks(Map<Integer, Set<Integer>> holdMap, Map<Integer, Set<Integer>> waitMap) {
        List<Integer> deadlocks = new ArrayList<>();
        for (int pid : waitMap.keySet()) {
            Set<Integer> waitSet = waitMap.get(pid);
            for (int resource : waitSet) {
                if (isResourceHeldByOthers(resource, pid, holdMap)) {
                    deadlocks.add(pid);
                    break;
                }
            }
        }
        return deadlocks;
    }

    private static boolean isResourceHeldByOthers(int resource, int pid, Map<Integer, Set<Integer>> holdMap) {
        for (int otherPid : holdMap.keySet()) {
            if (otherPid != pid && holdMap.get(otherPid).contains(resource)) {
                return true;
            }
        }
        return false;
    }
}

