import java.util.*;

public class Main {
    public static void main(String[] args) {
        Queue<Integer> queue = new LinkedList<>(Arrays.asList(3, 2, 1, 4, 5));
        System.out.println(maxOperations(queue));
    }

    public static int maxOperations(Queue<Integer> queue) {
        PriorityQueue<Integer> pq = new PriorityQueue<>(queue);
        int operations = 0;
        while (!queue.isEmpty()) {
            int num = queue.poll();
            if (num == pq.peek()) {
                pq.poll();
            } else {
                queue.offer(num);
            }
            operations++;
        }
        return operations;
    }
}

