public class Main {
    public static boolean validWordAbbreviation(String word, String abbr) {
        int i = 0, j = 0;
        while (i < word.length() && j < abbr.length()) {
            if (word.charAt(i) == abbr.charAt(j)) {
                ++i;
                ++j;
                continue;
            }
            if (abbr.charAt(j) <= '0' || abbr.charAt(j) > '9') {
                return false;
            }
            int start = j;
            while (j < abbr.length() && abbr.charAt(j) >= '0' && abbr.charAt(j) <= '9') {
                ++j;
            }
            int num = Integer.valueOf(abbr.substring(start, j));
            i += num;
        }
        return i == word.length() && j == abbr.length();
    }

    public static void main(String[] args) {
        System.out.println(validWordAbbreviation("word", "1ord")); // true
        System.out.println(validWordAbbreviation("word", "4")); // true
        System.out.println(validWordAbbreviation("word", "2r1")); // true
        System.out.println(validWordAbbreviation("word", "w1r1")); // true
        System.out.println(validWordAbbreviation("word", "word")); // true
        System.out.println(validWordAbbreviation("word", "1o2")); // false
        System.out.println(validWordAbbreviation("word", "wo3")); // false
    }
}

