        int[] sum;
        if (a.length > b.length) {
            sum = new int[a.length];
            for (int i = 0; i < a.length; i++) {
                if (i < b.length) {
                    sum[i] = a[i] + b[i];
                } else {
                    sum[i] = a[i];
                }
            }
        } else {
            sum = new int[b.length];
            for (int i = 0; i < b.length; i++) {
                if (i < a.length) {
                    sum[i] = a[i] + b[i];
                } else {
                    sum[i] = b[i];
                }
            }
        }
        return sum;
    }
