        String[] words = {"apple", "banana", "cherry", "date", "egg", "fig", "grape"};
        int left = 1;
        int right = 5;
        System.out.println(countVowelStrings(words, left, right));
    }

    public static int countVowelStrings(String[] words, int left, int right) {
        int count = 0;
        for (int i = left; i <= right; i++) {
            if (isVowelString(words[i])) {
                count++;
            }
        }
        return count;
    }

    private static boolean isVowelString(String word) {
        char firstChar = word.charAt(0);
        char lastChar = word.charAt(word.length() - 1);
        return isVowel(firstChar) && isVowel(lastChar);
    }

    private static boolean isVowel(char c) {
        return c == 'a' || c == 'e' || c == 'i' || c == 'o' || c == 'u';
    }
}

