import java.io.BufferedReader;
import java.io.BufferedWriter;
import java.io.FileReader;
import java.io.FileWriter;
import java.io.IOException;

public class CommentRemover {
    public static void removeCommentsFromFile(String inputFilePath, String outputFilePath) {
        try (BufferedReader reader = new BufferedReader(new FileReader(inputFilePath));
             BufferedWriter writer = new BufferedWriter(new FileWriter(outputFilePath))) {

            String line;
            StringBuilder codeBuilder = new StringBuilder();
            boolean isCommentBlock = false;

            while ((line = reader.readLine()) != null) {
                line = line.trim();

                // 处理多行注释块
                if (isCommentBlock) {
                    if (line.contains("*/")) {
                        line = line.substring(line.indexOf("*/") + 2);
                        isCommentBlock = false;
                    } else {
                        continue;
                    }
                }

                // 处理单行注释
                if (line.startsWith("//")) {
                    line = "";
                } else if (line.startsWith("/*")) {
                    // 处理多行注释块的起始行
                    if (line.contains("*/")) {
                        line = line.substring(line.indexOf("*/") + 2);
                    } else {
                        isCommentBlock = true;
                        continue;
                    }
                }

                codeBuilder.append(line);
                codeBuilder.append(System.lineSeparator());
            }

            writer.write(codeBuilder.toString());
        } catch (IOException e) {
            e.printStackTrace();
        }
    }

    public static void main(String[] args) {
        String inputFilePath = "Example.java";
        String outputFilePath = "CleanExample.java";

        removeCommentsFromFile(inputFilePath, outputFilePath);
    }
}
