import javax.imageio.ImageIO;
import java.awt.image.BufferedImage;

public class ImageClassifier {
    public static void classifyImages(String imageFolder, int threshold1, int threshold2){
        // 分类后的文件夹路径
        String outputFolder1 = imageFolder + "/folder_1";
        String outputFolder2 = imageFolder + "/folder_2";
        String outputFolder3 = imageFolder + "/folder_3";

        // 获取图像文件夹中的所有文件
        File[] imageFiles = new File(imageFolder).listFiles();
        if (imageFiles == null) {
            System.out.println("图像文件夹为空或无法访问。");
            return;
        }

        // 遍历图像文件夹中的所有文件
        for (File imageFile : imageFiles) {
            if (imageFile.getName().endsWith(".jpg") || imageFile.getName().endsWith(".png")) {
                try {
                    // 读取图像
                    BufferedImage image = ImageIO.read(imageFile);

                    // 获取图像的长宽
                    int width = image.getWidth();
                    int height = image.getHeight();

                    // 根据阈值分类
                    String outputFolder;
                    if (width < threshold1 || height < threshold1) {
                        outputFolder = outputFolder1;
                    } else if (width < threshold2 || height < threshold2) {
                        outputFolder = outputFolder2;
                    } else {
                        outputFolder = outputFolder3;
                    }

                    // 确保输出文件夹存在
                    new File(outputFolder).mkdirs();

                    // 将图像移动到相应的输出文件夹中
                    Path outputPath = new File(outputFolder, imageFile.getName()).toPath();
                    Files.copy(imageFile.toPath(), outputPath, StandardCopyOption.REPLACE_EXISTING);
                } catch (IOException e) {
                    System.out.println("处理图像文件时出错: " + e.getMessage());
                }
            }
        }
    }

    public static void main(String[] args) {
        // 图像文件夹路径
        String imageFolder = "images";

        // 阈值
        int threshold1 = 100;
        int threshold2 = 200;

        classifyImages(imageFolder, threshold1, threshold2);
    }
}
