import java.util.Arrays;
import java.util.HashSet;
import java.util.Set;

public class Distance {

    // 欧几里得距离
    public static double euclideanDistance(double[] point1, double[] point2) {
        double sum = 0;
        for (int i = 0; i < point1.length; i++) {
            sum += Math.pow(point1[i] - point2[i], 2);
        }
        return Math.sqrt(sum);
    }

    // 曼哈顿距离
    public static double manhattanDistance(double[] point1, double[] point2) {
        double sum = 0;
        for (int i = 0; i < point1.length; i++) {
            sum += Math.abs(point1[i] - point2[i]);
        }
        return sum;
    }

    // 切比雪夫距离
    public static double chebyshevDistance(double[] point1, double[] point2) {
        double max = 0;
        for (int i = 0; i < point1.length; i++) {
            max = Math.max(max, Math.abs(point1[i] - point2[i]));
        }
        return max;
    }

    // 闵可夫斯基距离
    public static double minkowskiDistance(double[] point1, double[] point2, double p) {
        double sum = 0;
        for (int i = 0; i < point1.length; i++) {
            sum += Math.pow(Math.abs(point1[i] - point2[i]), p);
        }
        return Math.pow(sum, 1 / p);
    }

    // 余弦相似度
    public static double cosineSimilarity(double[] vector1, double[] vector2) {
        double dotProduct = 0;
        double normVector1 = 0;
        double normVector2 = 0;

        for (int i = 0; i < vector1.length; i++) {
            dotProduct += vector1[i] * vector2[i];
            normVector1 += Math.pow(vector1[i], 2);
            normVector2 += Math.pow(vector2[i], 2);
        }

        return dotProduct / (Math.sqrt(normVector1) * Math.sqrt(normVector2));
    }

    // 汉明距离
    public static int hammingDistance(String string1, String string2) {
        int distance = 0;
        for (int i = 0; i < string1.length(); i++) {
            if (string1.charAt(i) != string2.charAt(i)) {
                distance++;
            }
        }
        return distance;
    }

    // Jaccard相似度
    public static double jaccardSimilarity(Set<Integer> set1, Set<Integer> set2) {
        Set<Integer> intersection = new HashSet<>(set1);
        intersection.retainAll(set2);

        Set<Integer> union = new HashSet<>(set1);
        union.addAll(set2);

        return (double) intersection.size() / union.size();
    }

    public static void main(String[] args) {
        // 手动编写数据
        double[] pointA = {1, 2, 3};
        double[] pointB = {4, 5, 6};

        double[] vectorX = {1, 2, 3};
        double[] vectorY = {4, 5, 6};

        String stringP = "1010101";
        String stringQ = "1001001";

        Set<Integer> setAlpha = new HashSet<>(Arrays.asList(1, 2, 3, 4, 5));
        Set<Integer> setBeta = new HashSet<>(Arrays.asList(4, 5, 6, 7, 8));

        // 调用函数并输出结果
        System.out.println("Euclidean Distance: " + euclideanDistance(pointA, pointB));
        System.out.println("Manhattan Distance: " + manhattanDistance(pointA, pointB));
        System.out.println("Chebyshev Distance: " + chebyshevDistance(pointA, pointB));
        System.out.println("Minkowski Distance (p=2): " + minkowskiDistance(pointA, pointB, 2));
        System.out.println("Cosine Similarity: " + cosineSimilarity(vectorX, vectorY));
        System.out.println("Hamming Distance: " + hammingDistance(stringP, stringQ));
        System.out.println("Jaccard Similarity: " + jaccardSimilarity(setAlpha, setBeta));
    }
}

