import org.w3c.dom.*;
import org.xml.sax.SAXException;

import javax.xml.parsers.DocumentBuilderFactory;
import javax.xml.parsers.ParserConfigurationException;
import javax.xml.transform.OutputKeys;
import javax.xml.transform.Transformer;
import javax.xml.transform.TransformerException;
import javax.xml.transform.TransformerFactory;
import javax.xml.transform.dom.DOMSource;
import javax.xml.transform.stream.StreamResult;
import java.io.File;
import java.io.IOException;

public class LabelConverter {
    public static double calculateIoU(int[] box1, int[] box2) {
        int x1 = box1[0];
        int y1 = box1[1];
        int x2 = box1[2];
        int y2 = box1[3];
        int x3 = box2[0];
        int y3 = box2[1];
        int x4 = box2[2];
        int y4 = box2[3];

        // 计算相交矩形的左上角和右下角坐标
        int inter_x1 = Math.max(x1, x3);
        int inter_y1 = Math.max(y1, y3);
        int inter_x2 = Math.min(x2, x4);
        int inter_y2 = Math.min(y2, y4);

        // 计算相交矩形的面积
        int inter_area = Math.max(0, inter_x2 - inter_x1 + 1) * Math.max(0, inter_y2 - inter_y1 + 1);

        // 计算并集面积和IoU
        int box1_area = (x2 - x1 + 1) * (y2 - y1 + 1);
        int box2_area = (x4 - x3 + 1) * (y4 - y3 + 1);
        int union_area = box1_area + box2_area - inter_area;
        double iou = (double) inter_area / union_area;

        return iou;
    }

    public static void convertLabels(String labelFile, int[] targetRegion) throws ParserConfigurationException, IOException, SAXException, TransformerException {
        DocumentBuilderFactory factory = DocumentBuilderFactory.newInstance();
        Document doc = factory.newDocumentBuilder().parse(new File(labelFile));

        NodeList objectList = doc.getElementsByTagName("object");

        for (int i = 0; i < objectList.getLength(); i++) {
            Element objectElem = (Element) objectList.item(i);
            Element bboxElem = (Element) objectElem.getElementsByTagName("bndbox").item(0);

            int xmin = Integer.parseInt(bboxElem.getElementsByTagName("xmin").item(0).getTextContent());
            int ymin = Integer.parseInt(bboxElem.getElementsByTagName("ymin").item(0).getTextContent());
            int xmax = Integer.parseInt(bboxElem.getElementsByTagName("xmax").item(0).getTextContent());
            int ymax = Integer.parseInt(bboxElem.getElementsByTagName("ymax").item(0).getTextContent());

            // 计算当前边界框与目标区域的IoU
            double iou = calculateIoU(targetRegion, new int[]{xmin, ymin, xmax, ymax});

            // 如果IoU大于0.2，则将类别设置为0
            if (iou > 0.2) {
                Element nameElem = (Element) objectElem.getElementsByTagName("name").item(0);
                nameElem.setTextContent("0");
            }
        }

        // 保存更新后的XML文件
        TransformerFactory transformerFactory = TransformerFactory.newInstance();
        Transformer transformer = transformerFactory.newTransformer();
        transformer.setOutputProperty(OutputKeys.INDENT, "yes");
        DOMSource source = new DOMSource(doc);
        StreamResult result = new StreamResult(new File(labelFile));
        transformer.transform(source, result);
    }

    public static void main(String[] args) {
        int[] targetRegion = {950, 786, 1400, 1052};
        String labelFile = "C:\\Users\\Administrator\\Desktop\\work\\1225\\annotations\\A37-145-BSD-231221-A_485.xml";

        try {
            convertLabels(labelFile, targetRegion);
            System.out.println("Label conversion completed successfully.");
        } catch (ParserConfigurationException | IOException | SAXException | TransformerException e) {
            System.out.println("An error occurred during label conversion: " + e.getMessage());
        }
    }
}
