import java.util.HashSet;

class User {
    private String name;
    private char gender;
    private String idNumber;

    // Constructor
    public User(String name, char gender, String idNumber) {
        this.name = name;
        this.gender = gender;
        this.idNumber = idNumber;
    }

    // Getter and setter methods

    public String getName() {
        return name;
    }

    public void setName(String name) {
        this.name = name;
    }

    public char getGender() {
        return gender;
    }

    public void setGender(char gender) {
        this.gender = gender;
    }

    public String getIdNumber() {
        return idNumber;
    }

    public void setIdNumber(String idNumber) {
        this.idNumber = idNumber;
    }

    // toString method
    @Override
    public String toString() {
        return String.format("Name:%s\nSex:%c\nID:%s", name, gender, idNumber);
    }
}

class UserManager {
    private HashSet<String> registeredIds = new HashSet<>();

    public String addUser(String name, String gender, String idNumber) {
        // Validate arguments
        if (!name.matches("[a-zA-Z_]+")) {
            return "Name illegal";
        }

        if (!gender.matches("[MFO]")) {
            return "Sex illegal";
        }

        if (!idNumber.matches("[0-9]{12}")) {
            return "ID number illegal";
        }

        // Extract and validate parts of the ID number
        int part1 = Integer.parseInt(idNumber.substring(0, 4));
        int part2 = Integer.parseInt(idNumber.substring(4, 8));
        int part3 = Integer.parseInt(idNumber.substring(8, 12));

        if (part1 < 1 || part1 > 1237 || part2 < 20 || part2 > 460 || part3 / 10 < 0 || part3 / 10 > 100) {
            return "ID number illegal";
        }

        // Check if the last digit of ID number matches the gender
        int lastDigit = part3 % 10;
        if ((gender.equals("M") && lastDigit != 1) || (gender.equals("F") && lastDigit != 0) || (gender.equals("O") && lastDigit != 2)) {
            return "ID number illegal";
        }

        // Check if ID number already registered
        if (registeredIds.contains(idNumber)) {
            return "ID number exist";
        }

        // All checks passed, add user
        User newUser = new User(name, gender.charAt(0), idNumber);
        registeredIds.add(idNumber);

        // Return formatted user information
        return newUser.toString();
    }
}

public class Main {
    public static void main(String[] args) {
        UserManager userManager = new UserManager();

        // Example usage
        System.out.println(userManager.addUser("LaoWang", "M", "003005001010")); // ID number illegal
        System.out.println(userManager.addUser("LaoWang", "M", "002301220991")); // Name:LaoWang\nSex:M\nID:002301220991
        System.out.println(userManager.addUser("HaiWang", "M", "002301220991")); // ID number exist
    }
}

