public class CopyFile {
    public static void copyAndMoveFile(String sourcePath, String destinationDirectory) {
        // 获取源文件的文件名
        Path sourceFilePath = Paths.get(sourcePath);
        String fileName = sourceFilePath.getFileName().toString();

        // 构建目标路径
