/*
创建一个名为LinearRegressionLoss的java类，编写三个静态方法用于计算线性回归中常用的三种损失函数均方误差，平均绝对误差和Huber损失，并在main方法中手动生成数据，调用这三个方法计算相应的损失，并输出相应的计算结果。
*/
public class LinearRegressionLoss {

    // Mean Squared Error (MSE)
    public static double calculateMSE(double[] actual, double[] predicted) {
        if (actual.length != predicted.length) {
            throw new IllegalArgumentException("Input arrays must have the same length");
        }

        double sumSquaredError = 0.0;

        for (int i = 0; i < actual.length; i++) {
            double error = actual[i] - predicted[i];
            sumSquaredError += Math.pow(error, 2);
        }

        return sumSquaredError / actual.length;
    }

    // Mean Absolute Error (MAE)
    public static double calculateMAE(double[] actual, double[] predicted) {
        if (actual.length != predicted.length) {
            throw new IllegalArgumentException("Input arrays must have the same length");
        }

        double sumAbsoluteError = 0.0;

        for (int i = 0; i < actual.length; i++) {
            double error = Math.abs(actual[i] - predicted[i]);
            sumAbsoluteError += error;
        }

        return sumAbsoluteError / actual.length;
    }

    // Huber Loss
    public static double calculateHuberLoss(double[] actual, double[] predicted, double delta) {
        if (actual.length != predicted.length) {
            throw new IllegalArgumentException("Input arrays must have the same length");
        }

        double sumHuberLoss = 0.0;

        for (int i = 0; i < actual.length; i++) {
            double error = Math.abs(actual[i] - predicted[i]);
            if (error <= delta) {
                sumHuberLoss += 0.5 * Math.pow(error, 2);
            } else {
                sumHuberLoss += delta * (error - 0.5 * delta);
            }
        }

        return sumHuberLoss / actual.length;
    }

    public static void main(String[] args) {
        // Sample data
        double[] actual = {3.0, 5.0, 7.0, 9.0, 11.0};
        double[] predicted = {2.5, 5.5, 7.5, 8.5, 10.5};

        // Calculate MSE
        double mse = calculateMSE(actual, predicted);
        System.out.println("Mean Squared Error (MSE): " + mse);

        // Calculate MAE
        double mae = calculateMAE(actual, predicted);
        System.out.println("Mean Absolute Error (MAE): " + mae);

        // Calculate Huber Loss with delta = 1.0
        double delta = 1.0;
        double huberLoss = calculateHuberLoss(actual, predicted, delta);
        System.out.println("Huber Loss (delta = " + delta + "): " + huberLoss);
    }
}






