import java.time.LocalDate;
import java.time.format.DateTimeFormatter;
import java.time.temporal.ChronoUnit;

public class DaysBetweenDates {

    public static long calculateDaysBetweenDates(String date1, String date2) {
        DateTimeFormatter formatter = DateTimeFormatter.ofPattern("yyyy-MM-dd");
        LocalDate startDate = LocalDate.parse(date1, formatter);
        LocalDate endDate = LocalDate.parse(date2, formatter);

        return ChronoUnit.DAYS.between(startDate, endDate);
    }

    public static void main(String[] args) {
        String date1 = "2023-03-15";
        String date2 = "2023-04-14";

        long days = calculateDaysBetweenDates(date1, date2);
        System.out.println("Days between dates: " + days);
    }
}

