public class MaxLCMGCD {

    // 计算最大公约数（GCD）
    public static int gcd(int a, int b) {
        if (b == 0) {
            return a;
        }
        return gcd(b, a % b);
    }

    // 计算最小公倍数（LCM）
    public static long lcm(int a, int b) {
        return (long) a / gcd(a, b) * b;
    }

    // 找出最大的 lcm(a, b) - gcd(a, b)
    public static long maxLCMMinusGCD(int n) {
        if (n == 1) return 0;
        int a = n;
        int b = n - 1;
        return lcm(a, b) - gcd(a, b);
    }

    public static void main(String[] args) {
        int n = 10; // 示例输入
        System.out.println("Maximum of lcm(a, b) - gcd(a, b) is: " + maxLCMMinusGCD(n));
    }
}

